import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import BackButton from "./Backbutton";
import DeletePersonal from "./delettions";
// import SearchFilter from "./search";

const DFP1 = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [updateId, setUpdateId] = useState(null);
  const [updateData, setUpdateData] = useState({
    quantity: "",
    price: "",
    dameg: "",
    timestamp: "",
  });
  const [searchQuery, setSearchQuery] = useState(""); // State to hold search query

  const navigate = useNavigate();

  useEffect(() => {
    if (!localStorage.getItem("userID")) {
      navigate("/");
    }
  }, [navigate]);

  useEffect(() => {
    fetchData();
  }, []); // Fetch data on mount

  const fetchData = async () => {
    try {
      const response = await fetch("/auth/personal1", {
        method: "GET",
      });

      if (response.ok) {
        const responseData = await response.json();
        setData(responseData.data);
        setLoading(false);
      } else {
        console.error("Error fetching data:", response.statusText);
      }
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const handlePrint = () => {
    window.print();
  };

  const goTo = () => {
    navigate("/Fetchalldata1");
  };

  const handleUpdate = (id) => {
    setUpdateId(id);
    const selectedItem = data.find((item) => item._id === id);
    setUpdateData({
      quantity: selectedItem.quantity,
      price: selectedItem.price,
      dameg: selectedItem.dameg,
      timestamp: selectedItem.timestamp,
    });
  };

  const cancelUpdate = () => {
    setUpdateId(null);
    setUpdateData({
      quantity: "",
      price: "",
      dameg: "",
      timestamp: "",
    });
  };

  const updatePersonalItem = async () => {
    try {
      const response = await fetch(`/auth/personal1/${updateId}`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        // Refresh data after updating
        fetchData();

        // Reset update state
        cancelUpdate();
      } else {
        console.error("Error updating data:", response.statusText);
      }
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  // Filter data based on search query
  const filteredData = data.filter((item) => {
    // Check if the search query is a valid date
    const searchDate = new Date(searchQuery);
    if (!isNaN(searchDate.getTime())) {
      const itemDate = new Date(item.timestamp);
      // Compare item's timestamp with search date
      return itemDate.toDateString() === searchDate.toDateString();
    }
    // If the search query is not a valid date, perform regular string search
    return Object.values(item).some(
      (value) =>
        typeof value === "string" &&
        value.toLowerCase().includes(searchQuery.toLowerCase()),
    );
  });

  return (
    <div>
      <BackButton />
      <button className="buttons" onClick={goTo}>
        Total
      </button>

      <button className="buttons" onClick={handlePrint}>
        Print
      </button>
      {/* <DeletePersonal /> */}

      <p>
        <h1 className="patched-data">Patched Data</h1>
      </p>

      {/* Search input field */}
      <input
        type="text"
        placeholder="Search..."
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />

      {loading ? (
        <p className="server-m">
          Server is not running .... <p className="loading-ani-server"></p>
        </p>
      ) : (
        <div className="data-container-dfp">
          <DeletePersonal />
          {/* <SearchFilter /> */}
          {/* <h1 className="totl-data">Patched Data:</h1> */}
          {filteredData.length > 0 ? (
            <table className="data-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>pername</th>
                  <th>quantity</th>
                  <th>price</th>
                  <th>dameg</th>
                  <th>Timestamp</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((i) => (
                  <tr key={i._id}>
                    <td>{i._id}</td>
                    <td>{i.pername}</td>
                    <td>
                      {updateId === i._id ? (
                        <input
                          type="text"
                          placeholder={i.quantity}
                          value={updateData.quantity}
                          onChange={(e) =>
                            setUpdateData({
                              ...updateData,
                              quantity: e.target.value,
                            })
                          }
                        />
                      ) : (
                        i.quantity
                      )}
                    </td>
                    <td>
                      {updateId === i._id ? (
                        <input
                          type="text"
                          placeholder={i.price}
                          value={updateData.price}
                          onChange={(e) =>
                            setUpdateData({
                              ...updateData,
                              price: e.target.value,
                            })
                          }
                        />
                      ) : (
                        i.price
                      )}
                    </td>
                    <td>
                      {updateId === i._id ? (
                        <input
                          type="text"
                          placeholder={i.dameg}
                          value={updateData.dameg}
                          onChange={(e) =>
                            setUpdateData({
                              ...updateData,
                              dameg: e.target.value,
                            })
                          }
                        />
                      ) : (
                        i.dameg
                      )}
                    </td>
                    <td>
                      {updateId === i._id ? (
                        <input
                          type="text"
                          placeholder={i.timestamp}
                          value={updateData.timestamp}
                          onChange={(e) =>
                            setUpdateData({
                              ...updateData,
                              timestamp: e.target.value,
                            })
                          }
                        />
                      ) : (
                        i.timestamp
                      )}
                    </td>
                    <td>
                      {updateId === i._id ? (
                        <>
                          <button
                            className="update-ch1"
                            onClick={updatePersonalItem}
                          >
                            Save
                          </button>
                          <button className="update-ch2" onClick={cancelUpdate}>
                            Cancel
                          </button>
                        </>
                      ) : (
                        <button
                          className="update"
                          onClick={() => handleUpdate(i._id)}
                        >
                          Update
                        </button>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          ) : (
            <p>No data available</p>
          )}
        </div>
      )}
    </div>
  );
};

export default DFP1;
