import React, { useState } from 'react';
import axios from 'axios';

const UserProfile = ({ userId }) => {
  const [deleteStatus, setDeleteStatus] = useState(null);

  const handleDeleteAccount = async () => {
    try {
      // Make a DELETE request to the server's delete endpoint
      const response = await axios.delete(`/auth/p1-auths/${userId}`);

      // If the deletion is successful, update the state
      setDeleteStatus(response.data.message);
    } catch (error) {
      console.error('Error deleting account:', error.message);
      setDeleteStatus('An error occurred while deleting the account.');
    }
  };

  return (
    <div>
      <h1>User Profile</h1>
      <p>User ID: {userId}</p>
      {deleteStatus && <p>{deleteStatus}</p>}
      <button onClick={handleDeleteAccount}>Delete Account</button>
    </div>
  );
};

export default UserProfile;
